/**
 * class LinkView : path-based representation of a link between two memory units
 * This view uses the Raphael library (it aggregates a Raphael Object)
 * @param ctx : "drawing context" (Raphael instance)
 * @param sourceUnitView1 : a view on the source memory unit
 * @param destUnitView2 : a view on the destination memory unit
 * @author michael 
 */
 
 define("LinkView",
[],
function() {

var LinkView = function(ctx, sourceUnit, destUnit){

	var linkObject;
	var linkObjectPath;
	
	this.sourceUnit = sourceUnit;
	this.destUnit = destUnit;

	var obj2 = destUnit.getView().getBoxObject();
	
	
	var obj1 = sourceUnit.getView().getBoxObject();
			

	var bg = LinkView.LINE_WIDTH;
	var line = LinkView.LINE_COLOR;
	
	this.clear = function(){
		linkObjectPath.remove();
	}
	

	this.update = function(obj1, obj2, line, bg) {
		
	
		if(!obj1){
			line = linkObject;
			obj1 = line.from;
			obj2 = line.to;
		}

		
		var bb1 = obj1.getBBox(),
			bb2 = obj2.getBBox(),
			p = [{x: bb1.x + bb1.width / 2, y: bb1.y - 1},
			{x: bb1.x + bb1.width / 2, y: bb1.y + bb1.height + 1},
			{x: bb1.x - 1, y: bb1.y + bb1.height / 2},
			{x: bb1.x + bb1.width + 1, y: bb1.y + bb1.height / 2},
			{x: bb2.x + bb2.width / 2, y: bb2.y - 1},
			{x: bb2.x + bb2.width / 2, y: bb2.y + bb2.height + 1},
			{x: bb2.x - 1, y: bb2.y + bb2.height / 2},
			{x: bb2.x + bb2.width + 1, y: bb2.y + bb2.height / 2}],
			d = {}, dis = [];
		for (var i = 0; i < 4; i++) {
			for (var j = 4; j < 8; j++) {
				var dx = Math.abs(p[i].x - p[j].x),
					dy = Math.abs(p[i].y - p[j].y);
				if ((i == j - 4) || (((i != 3 && j != 6) || p[i].x < p[j].x) && ((i != 2 && j != 7) || p[i].x > p[j].x) && ((i != 0 && j != 5) || p[i].y > p[j].y) && ((i != 1 && j != 4) || p[i].y < p[j].y))) {
					dis.push(dx + dy);
					d[dis[dis.length - 1]] = [i, j];
				}
			}
		}
		if (dis.length == 0) {
			var res = [0, 4];
		} else {
			res = d[Math.min.apply(Math, dis)];
		}
		var x1 = p[res[0]].x,
			y1 = p[res[0]].y,
			x4 = p[res[1]].x,
			y4 = p[res[1]].y;
		dx = Math.max(Math.abs(x1 - x4) / 2, 10);
		dy = Math.max(Math.abs(y1 - y4) / 2, 10);
		var x2 = [x1, x1, x1 - dx, x1 + dx][res[0]].toFixed(3),
			y2 = [y1 - dy, y1 + dy, y1, y1][res[0]].toFixed(3),
			x3 = [0, 0, 0, 0, x4, x4, x4 - dx, x4 + dx][res[1]].toFixed(3),
			y3 = [0, 0, 0, 0, y1 + dy, y1 - dy, y4, y4][res[1]].toFixed(3);
		var path = ["M", x1.toFixed(3), y1.toFixed(3), "C", x2, y2, x3, y3, x4.toFixed(3), y4.toFixed(3)].join(",");
		if (line && line.line) {
			line.bg && line.bg.attr({path: path});
			line.line.attr({path: path});
			
			
		} else {
			var color = typeof line == "string" ? line : "#000";
			
			linkObjectPath = ctx.path(path);
		
			linkObject = {
				bg: linkObjectPath.attr({"arrow-end" : "classic-wide-long", fill: "none", "stroke-width": bg}),
				line: linkObjectPath.attr({stroke: line, fill: "none"}),
				from: obj1,
				to: obj2
			};
			
			
			
	
			
		}
	};	
	
	this.getLinkObject = function(){
		return linkObjectPath;
	}
	
	this.update(obj1, obj2, line, bg);	

	this.getSourceUnit = function(){
		return this.sourceUnit;
	}
	
	this.getDestUnit = function(){
		return this.destUnit;
	}	

}

// static variables
LinkView.LINE_COLOR = "black";
LinkView.LINE_WIDTH = 2;

return LinkView;

});


